import React from 'react';
import {useNavigate} from 'react-router-dom';
import {LeftMedAd,RightMedAd} from '../components/mediumAd.js';
import useStateContext from '../hooks/useStateContext.js';
import useForm from '../hooks/useForm.js';
import { createAPIEndpoint, ENDPOINTS } from '../api';



const ticketreg = {
    title: "Purchase Single Tickets: $20",
    description: "Purchase tickets individually. Tickets include full 1-day access to all park amenities including rides, shows, and attractions.",
    link: "/tickets",
    buttonText: "Buy Single Tickets >"
}

const ticketgroup = {
    title: "Purchase Ticket Group Pack: 5 for $85",
    description: "Purchase group packs of tickets for a discounted price (5 pack)! Tickets include full 1-day access to all park amenities including rides, shows, and attractions.",
    link: "/map",
    buttonText: "Purchase Group Pack >"
}

const getFreshModel = () => ({
    tickets: 0,
    price: 0,
    date: "",
    email: "",
});

function Tickets() {
    const {context,setContext} = useStateContext();
    const navigate = useNavigate();
    const {values,setValues,errors,setErrors,handleInputChange} = useForm(getFreshModel);

    const buyTickets = (e) => {
        e.preventDefault();
    };

    if (context.account == "customer")
    {
        createAPIEndpoint(ENDPOINTS.customer+"/"+context.login_id)
        .fetch()
        .then(response => {
            values.email = response.data;
        })
        .catch(errors => console.log(errors))
    }

    return (
        <div className='form-page'>
            <div className='form-box'>
                <h2>Buy Tickets</h2>
                <hr style={{border:'2px solid white'}}/>
                <h4>Tickets include full 1-day access to all park amenities including rides, shows, and attractions.</h4>
                <div className="deal">
                    <h5>Special Discount:</h5>
                    <p>If you purchase 5 or more tickets, you'll get the group pack 15% discount! &#40;5 tickets will be $85 instead of $100!&#41;</p>
                </div>
                <br /><br />
                <div className='form-inner-box'>
                    <form name='buyTickets' id='buyTickets' method='post' onSubmit={buyTickets}>
                        <label>Tickets:</label><br />
                        <input type='number' name='tickets' onChange={handleInputChange} min='0' required />
                    </form>
                </div>
            </div>
        </div>
    );
}
    

export default Tickets;
