import React, {useState} from 'react';
import '../css/dataentry.css';
import '../css/reporttable.css';
import { createAPIEndpoint, ENDPOINTS } from '../api/index.js';

function CustomerSearch(){
    const [data, setData]= useState([]);

    const blankFilters = {
        id: "",
        fname: "",
        lname: "",
        height: "",
        year: "",
        month: "",
        tickets: -1,
    };

    const [filters, setFilters] = useState(blankFilters);
    const updateFilters = (obj) => {setFilters(...filters,...obj);}

    const findcustomer = () => {
        createAPIEndpoint(ENDPOINTS.customer)
        .fetch()
        .then(response => {
            setData(response.data)})
        .catch(error => console.log(error))
    }

    const renderTable = () =>{
        return data.map(elem => {
            if(elem.customer_id==values){
            return (
                <tr>
                    <td>{elem.customer_id}</td>
                    <td>{elem.fname}</td>
                    <td>{elem.lname}</td>
                    <td>{elem.email}</td>
                    <td>{elem.password}</td>
                    <td>{elem.height}</td>
                    <td>{elem.tickets_bought}</td>
                </tr>
            )}
        })
    }

    return (
        <div className='entry-form'>
            <h2>Customer Search</h2>
            <div className='form-inner-box'>
                <form name="customersearch" id="customersearch" onSubmit={findcustomer}>
                    <label>ID: </label>
                    <input type="text" name="customer_id" value={values} onChange={(e) => setvalue(e.target.value)} size="30"/><br />
                </form>
            </div> 
            <button onClick={findcustomer} className="submit-button" type="button">Search Customers</button>
            <br /><br />
            <br /><br />
            <table>
                <thead>
                <tr>
                    <th>Customer ID</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Password</th>
                    <th>Height</th>
                    <th>Tickets</th>
                </tr>
                </thead>
                <tbody>{renderTable()}</tbody>
            </table>
        </div>

    )

}

export default CustomerSearch;
