import React, {useState} from 'react';
import '../css/reporttable.css';
import EmployeeSearchBox from '../components/employeeSearchBox';
import EmployeeEntry from '../components/employeeEntry';

function EmployeeSearch() {
    const [data, setData] = useState([]);

    const getFromChild = (employees) => {
        setData(employees);
    }

    const renderTable = () => {
        return data.map(elem => {
            return (
                <tr key={elem.employee_id}>
                    <td>{elem.employee_id}</td>
                    <td>{elem.fname}</td>
                    <td>{elem.lname}</td>
                    <td>{new Date(elem.dob).toLocaleDateString()}</td>
                    <td>{elem.supervisor_id}</td>
                    <td>{elem.job_title}</td>
                    <td>{elem.username}</td>
                </tr>
            )
        })
    }

    return (
        <div className='searchpage'>
            <div className='optionbox'>
                {<EmployeeSearchBox returnData={getFromChild}/>}
                {<EmployeeEntry returnData={getFromChild}/>}
            </div>
            <br /><br />
            <br /><br />
            <table className="result-table">
                <thead>
                    <tr>
                        <th>Employee ID</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Date of Birth</th>
                        <th>Supervisor ID</th>
                        <th>Job Title</th>
                        <th>Username</th>
                    </tr>
                </thead>
                <tbody>{renderTable()}</tbody>
            </table>
        </div>

    );
}

export default EmployeeSearch;
