import React from 'react';
import {Link} from 'react-router-dom';
import '../css/home.css';

function SmallAd(props) {
    return (
        <div className="smallad-container" style={{backgroundColor: props.value.color, float: props.value.align}}>
            <div className="smallad-content">
                <div className="smallad-text">
                    <h2>{props.value.title}</h2>
                    <p>{props.value.description}</p>
                </div>
                <Link className="ad-button" to={props.value.link}>{props.value.buttonText}</Link>
            </div>
        </div>
    );
}

export default SmallAd;
