import React, {useState} from 'react';
import '../css/reporttable.css';
import { createAPIEndpoint, ENDPOINTS } from '../api/index.js';

function StatsMonthly() {
    const [data, setData]= useState([]);

    const blankFilters = {
        year: "",
        month: ""
    };

    const [filters, setFilters] = useState(blankFilters);
    const updateFilters = (obj) => {setFilters({...filters,...obj});}

    const getreport = () => {
        createAPIEndpoint(ENDPOINTS.report+filters.month+'/'+filters.year)
        .fetch()
        .then(response => {
            console.log(response.data);
            setData(response.data)})
        .catch(error => console.log(error))
    }

    const renderTable = () => {
        let info = data;
        return info.map(elem => {
            return (
                <tr>
                    <td></td>
                </tr>
            );}
        )
    }

    return (
        <div className='searchbox'>
            <h2>Monthly Reports</h2>
            <div>
                <p>*Search by year and month.</p>
                <form name="ridesearch" id="ridesearch">
                    <table className="filter-table">
                        <tr>
                            <th>Year:</th>
                            <th>Month:</th>
                        </tr>
                        <tr>
                            <td><input type="number" name="year" onChange={(e) => updateFilters({id:e.target.value})} required/></td>
                            <td><input type="number" name="month" onChange={(e) => updateFilters({name:e.target.value})} required/></td>
                        </tr>
                    </table>
                </form>
            </div><br/>
            <button onClick={getreport} className="submit-button" type="button">Search Rides</button>
            <br /><br />
            <br /><br />
            <table className="result-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Location</th>
                        <th>Minimum Height</th>
                        <th>Start Time</th>
                        <th>End Time</th>
                        <th>Breakdowns</th>
                    </tr>
                </thead>
                <tbody>{renderTable()}</tbody>
            </table>
        </div>

    )
}

export default StatsMonthly;
