import React, {useState} from 'react';
import '../css/reporttable.css';
import useForm from '../hooks/useForm';
import { createAPIEndpoint, ENDPOINTS } from '../api';

function EmployeeEdit (props) {
    const getFreshModel = () => ({
        fname: "",
        lname: "",
        job_title: "",
        dob: "",
        supervisor_id: "",
        username: ""
    });

    const {values,setValues,errors,setErrors,handleInputChange} = useForm(getFreshModel);
    
    return (
        <td className="edit-box" colSpan="7">
            <p>Edit Employee {props.values.employee_id}</p>
            <form name="employeeedit" id="employeeedit">
                <div className="edit-form">
                    <div className="edit-form-item">
                        <label>New First Name: </label>
                        <input type="text" name="fname" value={values.fname} onChange={handleInputChange} required/>
                    </div>
                </div>
            </form>
            <button type="button" value={"edit-employee-"+props.values.employee_id} onClick={props.endEdit}>Close</button>
        </td>
    );
}

export default EmployeeEdit;
