import '../css/profilepage.css';
import { createAPIEndpoint, ENDPOINTS } from '../api/index.js';
import useForm from '../hooks/useForm.js';
import useStateContext from '../hooks/useStateContext.js';

const getFreshFormModel = () => ({
    fname: "",
    lname: "",
    height: "",
    email: "",
    dob: ""
});

